mapper.select:
select category_id,category_name,category_desc,create_user,create_time
from ndp_category
where 1=1

  and category_id = #{category_id}


  and category_name like concat(concat('%',#{category_name}),'%')


  and create_user = #{create_user}


  and create_time > #{startTime}


  and create_time < #{endTime}

select subject_id,category_id,subject_name,subject_desc,table_id,table_code
from ndp_subject
where 1=1

  and subject_id = #{subject_id}


  and category_id = #{category_id}


  and subject_name like concat(concat('%',#{subject_name}),'%')


  and table_id = #{table_id}


  and table_code = #{table_code}

select a.column_id,a.subject_id,a.column_name,a.cell_id,a.search_condition, a.search_setting,a.column_order,a.is_show,b.table_id,b.cell_code,b.cell_name, b.dict_id,b.data_unit,b.data_desc,c.dict_name,d.dimension_id,d.dimension_name, d.ref_table,d.key_field,d.name_field,d.show_field,d.dimension_type,e.subject_name,e.category_id, e.subject_name,e.subject_desc,e.table_code from ndp_subject_column a left join ndp_table_cell b on a.cell_id = b.cell_id left join ( select distinct dict_id,dict_name from ndp_dict ) c on b.dict_id = c.dict_id left join ndp_dimension d on a.search_condition = d.dimension_id left join ndp_subject e on a.subject_id = e.subject_id where 1=1
and a.is_show = #{is_show}
and a.subject_id = #{subject_id}
order by a.column_order asc
