mapper.select:
select subject_id as subjectId,subject_name as subjectName ,ns.category_id as categoryId,table_id as tableId,table_code as tableCode,nc.category_name as categoryName from ndp_subject ns,ndp_category nc where ns.CATEGORY_ID=nc.CATEGORY_ID
and subject_name like concat(concat('%',#{subjectName}),'%')
order by ${sort} ${order}

select subject_id as subjectId,subject_name as subjectName ,subject_desc as subjectDesc,ns.category_id as categoryId,table_id as tableId,table_code as tableCode,nc.category_name as categoryName
from  ndp_subject ns,ndp_category nc where ns.CATEGORY_ID=nc.CATEGORY_ID
and ns.subject_id =#{subjectId}

mapper.insert:
insert into ndp_subject (CATEGORY_ID, SUBJECT_NAME,
SUBJECT_DESC, TABLE_ID, TABLE_CODE,CREATE_TIME
)
values (#{categoryId,jdbcType=INTEGER}, #{subjectName,jdbcType=VARCHAR},
#{subjectDesc,jdbcType=VARCHAR}, #{tableId,jdbcType=INTEGER}, #{tableCode,jdbcType=VARCHAR}, #{createTime,jdbcType=CHAR}
)

insert into ndp_subject_column (SUBJECT_ID, COLUMN_NAME ,
CELL_ID, SEARCH_CONDITION, SEARCH_SETTING,COLUMN_ORDER,IS_SHOW
)
values (#{subjectId,jdbcType=INTEGER}, #{columnName,jdbcType=VARCHAR},
#{cellId,jdbcType=INTEGER}, #{searchCondition,jdbcType=VARCHAR}, #{searchSetting,jdbcType=VARCHAR},#{columnOrder,jdbcType=INTEGER},#{isShow,jdbcType=VARCHAR}
)

insert into ndp_subject (SUBJECT_ID, CATEGORY_ID, SUBJECT_NAME, 
  SUBJECT_DESC, TABLE_ID, TABLE_CODE
  )
values (#{subjectId,jdbcType=INTEGER}, #{categoryId,jdbcType=INTEGER}, #{subjectName,jdbcType=VARCHAR}, 
  #{subjectDesc,jdbcType=VARCHAR}, #{tableId,jdbcType=INTEGER}, #{tableCode,jdbcType=VARCHAR}
  )
